import React, { useState } from "react";

export const CardPoke = ({ pokemon }) => {
  //const {pokemon} = props;
  console.log("prop", pokemon);
  const [pokemonSelected, setPokemonSelected] = useState(pokemon);
  console.log("pokemonSelect", pokemonSelected);

  const hp = pokemon.stats[0]?.base_stat;
  const atk = pokemon.stats[1]?.base_stat;
  const def = pokemon.stats[2]?.base_stat;
  const specialAtk = pokemon.stats[3]?.base_stat;
  const specialDef = pokemon.stats[4]?.base_stat;
  const speed = pokemon.stats[5]?.base_stat;

  console.log("hp pokemon", hp);
  //const hp2 = pokemonSelected.pokemon[0].stats[0].base_stat;
  //console.log('hp2 pokemonSelected', hp2);

  const porcent = (stat) => {
    const max = 255;

    return stat / max;
  };

  return (
    <div>
      <h1>CardPoke</h1>
      <div>
        {pokemon.stats.map((stat) => {
          return (
            <p>
              {stat.stat.name} : {stat.base_stat};
            </p>
          );
        })}
      </div>
    </div>
  );
};
